import 'package:flutter/material.dart';
import 'package:google_fonts/google_fonts.dart';

class PricingView extends StatefulWidget {
  static String name = "";
  static String opinion = "";

  @override
  State<PricingView> createState() => _PricingViewState();
}

class _PricingViewState extends State<PricingView> {
  OverlayEntry? _overlayEntry;
  bool _isMenuOpen = false;
  List<Map<String, dynamic>> dataMap = [
    {
      "name": "Centro Mar√≠a Auxiliadora",
      "image":
          "data:image/jpeg;base64,",
      "data": {
        "info1": "fsdfas",
        "info2": "sa fsfaf sf a",
        "info3": "fs osah ks sg ",
        "info4": " as s asf ssf",
      },
    }
  ];
  @override
  Widget build(BuildContext context) {
    final Size size = MediaQuery.of(context).size;
    return GestureDetector(
      onTap: () => closeMenu(),
      child: Container(
        color: Colors.grey,
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Center(
              child: FittedBox(
                fit: BoxFit.contain,
                child: Text(
                  'Mapa de Centros',
                  style: GoogleFonts.abhayaLibre(
                    fontSize: 50,
                    fontWeight: FontWeight.bold,
                  ),
                ),
              ),
            ),
            Center(
              child: Stack(
                children: [
                  Container(
                    decoration: BoxDecoration(
                      color: Colors.white,
                      border: Border.all(
                        color: const Color.fromARGB(255, 13, 71, 161),
                        width: 3,
                      ),
                    ),
                    margin: EdgeInsets.symmetric(horizontal: 10, vertical: 10),
                    height: size.height * 0.85,
                    width: size.width * 0.5,
                    child: Image.network(
                        "https://mapademexicosinnombres.com/wp-content/uploads/2023/03/Mapa-de-Colombia-a-color-y-con-nombres.png"),
                  ),
                  Positioned(
                    height: size.height * 0.81,
                    left: size.width * 0.212,
                    child: Container(
                      child: GestureDetector(
                        onTap: () {
                          openMenu(size, "Bogot√°", dataMap);
                        },
                        child: MouseRegion(
                          cursor: SystemMouseCursors.click,
                          child: Icon(
                            Icons.location_on_outlined,
                            color: Colors.amber,
                            size: size.height * 0.04,
                          ),
                        ),
                      ),
                    ),
                  )
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }

  void closeMenu() {
    try {
      _overlayEntry?.remove();
      _overlayEntry = null;
    } catch (_) {}
    _isMenuOpen = !_isMenuOpen;
  }

  void openMenu(Size size, String title, List<Map<String, dynamic>> data) {
    _overlayEntry = _overlayEntryBuilder(size, title, data);
    Overlay.of(context).insert(_overlayEntry!);
    _isMenuOpen = !_isMenuOpen;
  }

  OverlayEntry _overlayEntryBuilder(
    Size size,
    String title,
    List<Map<String, dynamic>> data,
  ) {
    return OverlayEntry(
      builder: (context) {
        return Positioned(
          left: 50,
          top: 20,
          width: size.width * 0.2,
          height: size.height * 0.5,
          child: GestureDetector(
            onTap: () => closeMenu(),
            child: Material(
              color: Colors.transparent,
              child: Container(
                decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(20),
                  color: Colors.white,
                ),
                child: Container(
                  margin: EdgeInsets.symmetric(horizontal: 10, vertical: 10),
                  child: Column(
                    children: [
                      Text(
                        title,
                        style: TextStyle(
                          color: const Color.fromARGB(255, 13, 71, 161),
                          fontWeight: FontWeight.bold,
                          fontSize: 20,
                        ),
                      ),
                      SizedBox(
                        height: size.height * 0.01,
                      ),
                      Container(
                        height: size.height * 0.43,
                        width: size.width * 0.18,
                        child: ListView.builder(
                          itemCount: data.length,
                          itemBuilder: (context, index) {
                            return ListTile(
                              leading: Icon(Icons.church),
                              title: Text(data[index]["name"]),
                              onTap: () {
                                closeMenu();
                                showDialog(
                                  barrierDismissible: true,
                                  context: context,
                                  builder: (context) {
                                    return InfoAlert(
                                      size: size,
                                      title: data[index]["name"],
                                      info: data[index]["data"],
                                      image: data[index]["image"],
                                    );
                                  },
                                );
                              },
                            );
                          },
                        ),
                      ),
                    ],
                  ),
                ),
              ),
            ),
          ),
        );
      },
    );
  }
}

class InfoAlert extends StatelessWidget {
  const InfoAlert({
    Key? key,
    required this.size,
    required this.title,
    required this.image,
    required this.info,
  }) : super(key: key);

  final Size size;
  final String title;
  final String image;

  final Map<String, dynamic> info;

  @override
  Widget build(BuildContext context) {
    return WillPopScope(
      onWillPop: null,
      child: AlertDialog(
        shape: RoundedRectangleBorder(
          borderRadius: BorderRadius.circular(20),
        ),
        contentPadding: EdgeInsets.zero,
        content: Container(
          height: size.height * 0.7,
          width: size.width * 0.5,
          padding: const EdgeInsets.symmetric(horizontal: 50),
          constraints: const BoxConstraints(maxWidth: 1000),
          child: SingleChildScrollView(
            child: Column(
              mainAxisAlignment: MainAxisAlignment.spaceAround,
              crossAxisAlignment: CrossAxisAlignment.center,
              children: <Widget>[
                Container(
                  child: Text(
                    title,
                    style: TextStyle(
                      fontSize: 30,
                      fontWeight: FontWeight.bold,
                      color: const Color.fromARGB(255, 13, 71, 161),
                    ),
                  ),
                ),
                SizedBox(
                  height: size.height * 0.05,
                ),
                Container(
                  height: size.height * 0.5,
                  color: Colors.red,
                  child: Image.network(
                    image,
                    fit: BoxFit.cover,
                  ),
                ),
                SizedBox(
                  height: size.height * 0.02,
                ),
                Row(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: [
                    Container(
                      width: size.width * 0.1,
                      margin: const EdgeInsets.symmetric(
                          vertical: 10, horizontal: 20),
                      child: ElevatedButton(
                        style: ElevatedButton.styleFrom(
                          backgroundColor: Colors.amber,
                          padding: const EdgeInsets.fromLTRB(5, 20, 5, 20),
                          shape: RoundedRectangleBorder(
                            borderRadius: BorderRadius.circular(30),
                          ),
                        ),
                        child: Text(
                          "cerrar".toUpperCase(),
                          style: const TextStyle(
                            color: Colors.black,
                            fontWeight: FontWeight.w700,
                          ),
                        ),
                        onPressed: () async {
                          Navigator.pop(context, false);
                        },
                      ),
                    ),
                  ],
                ),
              ],
            ),
          ),
        ),
      ),
    );
  }

  // Future<bool> _onWillPop(BuildContext context) async {
  //   Navigator.pop(context);
  //   return false;
  // }
}
